package com.example.swt.custom.widget;

import org.eclipse.swt.SWT;
import org.eclipse.swt.custom.StyledText;
import org.eclipse.swt.events.SelectionAdapter;
import org.eclipse.swt.events.SelectionEvent;
import org.eclipse.swt.layout.RowData;
import org.eclipse.swt.layout.RowLayout;
import org.eclipse.swt.widgets.Button;
import org.eclipse.swt.widgets.Display;
import org.eclipse.swt.widgets.Shell;

public class StyledTextExample {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		Display display = new Display();
		Shell shell = new Shell(display);
		createContents(shell);
		shell.open();
		shell.layout();
		while (!shell.isDisposed()) {
			if (!display.readAndDispatch()) {
				display.sleep();
			}
		}
	}

	public static void createContents(Shell shell) {
		shell.setLayout(new RowLayout(SWT.VERTICAL));

		final StyledText text = new StyledText(shell, SWT.MULTI);
		text.setLayoutData(new RowData(400, 400));

		Button button = new Button(shell, SWT.PUSH);
		button.setText("Append");
		button.addSelectionListener(new SelectionAdapter() {
			/*
			 * (non-Javadoc)
			 * 
			 * @see org.eclipse.swt.events.SelectionAdapter#widgetSelected(org.eclipse.swt.events.SelectionEvent)
			 */
			public void widgetSelected(SelectionEvent e) {
				text.append(text.getLineDelimiter());
				int pos = text.getLineCount() - 1;
				text.append("Try, Try again. " + pos);
				text.append(text.getLineDelimiter());
				text.setLineAlignment(pos, 1, SWT.CENTER);
			}
		});
	}
}
