/**
 * This is a simple enum which is used for boolean columns that are represented
 * by 'Y' or 'N' chars.
 */
public enum YesNoColumn {

	/**
	 * {@link {@link Boolean#FALSE}}
	 */
	NO("N"),

	/**
	 * {@link {@link Boolean#TRUE}}
	 */
	YES("Y");

	private final String yesNoFlag;

	/**
	 * Private constructor
	 * @param yesNoFlag
	 */
	private YesNoColumn(String yesNoFlag) {
		this.yesNoFlag = yesNoFlag;
	}

	/*
	 * (non-Javadoc)
	 * 
	 * @see java.lang.Enum#toString()
	 */
	@Override
	public String toString() {
		return yesNoFlag;
	}

	public boolean getBoolean() {
		return false;
	}
}
