package logParserProject.testControl;


import java.io.File;
import java.util.HashMap;
import java.util.Iterator;
import java.util.List;
import java.util.Map;
import java.util.Vector;

import org.eclipse.hyades.logging.parsers.importer.IImportWizardControl;
import org.eclipse.hyades.logging.parsers.importer.IImportWizardControlListener;
import org.eclipse.jface.dialogs.IDialogConstants;
import org.eclipse.swt.SWT;
import org.eclipse.swt.events.ModifyEvent;
import org.eclipse.swt.events.ModifyListener;
import org.eclipse.swt.events.SelectionEvent;
import org.eclipse.swt.events.SelectionListener;
import org.eclipse.swt.layout.GridData;
import org.eclipse.swt.layout.GridLayout;
import org.eclipse.swt.widgets.Button;
import org.eclipse.swt.widgets.Combo;
import org.eclipse.swt.widgets.Composite;
import org.eclipse.swt.widgets.Control;
import org.eclipse.swt.widgets.FileDialog;
import org.eclipse.swt.widgets.Label;
import org.eclipse.swt.widgets.Text;

public class TestControl implements IImportWizardControl {

	Text path;

	Combo version;

	Button btn;

	Map input = new HashMap();

	List listeners = new Vector();

	public Map getUserInput() {
		input.put("file_path_1", path.getText());
		input.put("version_1", version.getText());
		return input;
	}

	public boolean isValid() {		
		if(path.getText().trim().length()>0 && version.getText().trim().length()>0){
			return true;
		}
		return false;
	}

	public void setUserInput(Map input) {
		String logPath = (String)input.get("file_path_1");
		if(logPath!=null){
			path.setText(logPath);
		}
		String logVersion = (String)input.get("version_1");
		if(logVersion!=null){
			version.setText(logVersion);
		}
	}

	public void addControlListener(IImportWizardControlListener listener) {
		if(!listeners.contains(listener))
			listeners.add(listener);

	}

	public void removeControlListener(IImportWizardControlListener listener) {
		listeners.remove(listener);
	}

	public Control createContents(Composite parent, String arg1) {
		Composite details = new Composite(parent, SWT.NONE);
		GridLayout layout = new GridLayout();
		layout.numColumns = 3;		
		GridData data = new GridData();
		data.horizontalAlignment = GridData.FILL;		
		data.grabExcessHorizontalSpace = true;
		details.setLayout(layout);
		details.setLayoutData(data);

		Label label = new Label(details, SWT.NONE | SWT.WRAP);
		data = new GridData();
		label.setLayoutData(data);
		label.setText("Custom control log path:");

		path = new Text(details, SWT.BORDER);
		data = new GridData();
		data.widthHint = IDialogConstants.ENTRY_FIELD_WIDTH;
		path.setLayoutData(data);
		path.addModifyListener(new ModifyListener() {

			public void modifyText(ModifyEvent e) {
				for (Iterator iter = listeners.iterator(); iter.hasNext();) {
					IImportWizardControlListener element = (IImportWizardControlListener) iter.next();
					element.controlChanged();
				}
			}

		});
		path.setToolTipText("Enter the absolute path of the log file.");

		btn = new Button(details, SWT.PUSH);
		data = new GridData();
		data.grabExcessHorizontalSpace = true;
		data.horizontalAlignment = SWT.RIGHT;
		btn.setLayoutData(data);
		btn.setText("Browse...");
		btn.addSelectionListener(new SelectionListener() {

			public void widgetSelected(SelectionEvent e) {
                FileDialog fileDialog = new FileDialog(btn.getParent().getShell());
                fileDialog.setFilterPath(path.getText().trim());
                fileDialog.setFilterExtensions(new String[] { "*.log", "*" });
                fileDialog.open();

                String fileName = fileDialog.getFileName();

                if ((fileName != null) && (!fileName.trim().equals("")))
                    path.setText(fileDialog.getFilterPath().concat(File.separator).concat(fileName));


			}

			public void widgetDefaultSelected(SelectionEvent e) {
				// TODO Auto-generated method stub

			}

		});

		label = new Label(details, SWT.NULL | SWT.WRAP);
		data = new GridData();
		label.setLayoutData(data);
		label.setText("Custom control log version:");

		version = new Combo(details, SWT.BORDER);
		data = new GridData();
//		data.grabExcessHorizontalSpace = true;
//		data.widthHint = IDialogConstants.ENTRY_FIELD_WIDTH;
		version.setLayoutData(data);
		version.add("1.0");
		version.add("1.1");
		version.select(0);
		version.setToolTipText("The version of the log.");

		return details;
	}

	public String getLogElementLabel() {

		return path.getText();
	}



}

