		String p = "/org.eclipse.ui/buildnotes_workbench.html";
		IFile file = ResourcesPlugin.getWorkspace().getRoot().getFile(
				new Path(p));
		if (file.exists()) {
			BufferedInputStream originalContents = null;
			try {
				originalContents = new BufferedInputStream(file.getContents());
				StringBuffer buf = new StringBuffer();
				int ch;
				while ((ch = originalContents.read()) != -1) {
					buf.append((char) ch);
				}
				System.out.println(buf);
				// update buf
				ByteArrayInputStream c = new ByteArrayInputStream(buf
						.toString().getBytes());
				// pass in real progress monitor
				file.setContents(c, true, true, null);
			} catch (CoreException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			} catch (IOException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			} finally {
				if (originalContents != null) {
					try {
						originalContents.close();
					} catch (IOException e) {
						e.printStackTrace();
					}
				}
			}
		}
