This sample code gets NullPointerException when trying to scroll
with scrollbar thumb.


import org.eclipse.swt.*;
import org.eclipse.swt.widgets.*;
public class test {
   public static void main(String[] args) {
      Display d = new Display();
      Shell s = new Shell(d, SWT.SHELL_TRIM | SWT.H_SCROLL | SWT.V_SCROLL);
      s.open();
      while (!s.isDisposed()) {
         if (!d.readAndDispatch())
            d.sleep();
      }
      d.dispose();
   }

}
