import org.eclipse.birt.report.engine.api.IReportEngine;
import org.eclipse.birt.report.engine.api.EngineConfig;
import org.eclipse.birt.report.engine.api.IReportEngineFactory;
import org.eclipse.birt.report.engine.api.IRunAndRenderTask;
import org.eclipse.birt.report.engine.api.HTMLRenderContext;
import org.eclipse.birt.report.engine.api.EngineConstants;
import org.eclipse.birt.report.engine.api.HTMLRenderOption;
import org.eclipse.birt.report.engine.api.IReportRunnable;
import org.eclipse.birt.core.framework.Platform;

import java.util.logging.Level;
import java.util.concurrent.BlockingQueue;
import java.util.concurrent.ArrayBlockingQueue;
import java.util.concurrent.ThreadPoolExecutor;
import java.util.concurrent.TimeUnit;
import java.util.ArrayList;
import java.util.HashMap;
import java.io.ByteArrayOutputStream;

public class BugTest {

    private IReportEngine engine;
    private IReportRunnable design;

    public BugTest(IReportEngine engine, IReportRunnable design) {
        this.engine = engine;
        this.design = design;
    }

    public void runTest(int unitCount, int threadCount) {
        BlockingQueue<Runnable> queue = new
ArrayBlockingQueue<Runnable>(unitCount);

        ArrayList<ThreadTestUnit> units = new
ArrayList<ThreadTestUnit>(unitCount);
        for (int i = 0; i < unitCount; i++) {
            ThreadTestUnit unit = new ThreadTestUnit();
            units.add(unit);
        }

        ThreadPoolExecutor executor = new ThreadPoolExecutor(threadCount,
threadCount, Integer.MAX_VALUE, TimeUnit.SECONDS, queue);

        for (ThreadTestUnit unit : units) {
            executor.execute(unit);
        }

        executor.shutdown();
        try {
            executor.awaitTermination(Long.MAX_VALUE, TimeUnit.SECONDS);
        } catch (InterruptedException e1) {
            throw new RuntimeException(e1);
        }
    }


    public static void main(String[] args) {

        String birtRuntimeDir = "F:/birt-runtime-2_2_2/ReportEngine";
        String logDir = "f:/temp";
        String reportSrc = "F:/birt/new_report_2.rptdesign";

        IReportEngine engine;
        IReportRunnable design;

        try {
            EngineConfig config = new EngineConfig();
            config.setEngineHome(birtRuntimeDir);
            config.setLogConfig(logDir, Level.FINE);
            Platform.startup(config);
            IReportEngineFactory factory = (IReportEngineFactory) Platform

.createFactoryObject(IReportEngineFactory.EXTENSION_REPORT_ENGINE_FACTORY);
            engine = factory.createReportEngine(config);
            engine.changeLogLevel(Level.WARNING);
            design = engine.openReportDesign(reportSrc);
        } catch (Exception e) {
            throw new RuntimeException(e);
        }

        new BugTest(engine, design).runTest(600, 600);

        engine.shutdown();
        Platform.shutdown();
    }

    private class ThreadTestUnit implements Runnable {

        public void run() {
            try {
                IRunAndRenderTask task = engine.createRunAndRenderTask(design);
                HTMLRenderContext renderContext = new HTMLRenderContext();
                renderContext.setImageDirectory("image");
                HashMap contextMap = new HashMap();
                contextMap.put( EngineConstants.APPCONTEXT_HTML_RENDER_CONTEXT,
renderContext );
                task.setAppContext( contextMap );
                HTMLRenderOption options = new HTMLRenderOption();
                ByteArrayOutputStream str = new ByteArrayOutputStream();
                options.setOutputStream(str);
                options.setOutputFormat("html");
                task.setRenderOption(options);
                task.run();
                task.close();
            } catch (Exception e) {
                throw new RuntimeException(e);
            }
        }
    }
}



