#include <iostream>
#include <time.h>    // For nanosleep()
#include <signal.h>

#include "NetControllerMain/NCManager.hpp"
#include "Exception/ExceptionBase.hpp"
#include "Process/ProcessName.hpp"

bool gTerminate(false);    // Termination flag

void terminate (int aSignal)
{
   gTerminate = true;
}

int main(int argc, char *argv[])
{
   int   lSuccessCode = 0;   // Success
   Process::ProcessName *lProcessName;

   std::cout << "--------------------------------------------------" << 
std::endl;
   std::cout << "Starting NetController application... " << std::endl;

   ////////////////////////////
   // Verify input parameters
   ////////////////////////////
   if (argc < 2)
   {
      std::cout << "Usage " << argv[0] << " <configfile> " << std::endl;
      lSuccessCode = -1;  // Failure
   }
   else
   {
      try
      {
         lProcessName = new Process::ProcessName("NetController");
      }
      catch(Process::RuntimeException& aExc)
      {
         std::cout << "NetController is already running" << std::endl;
         gTerminate = true;
      }
      if (!gTerminate)
      {
         try
         {
            ////////////////////
            // Start processing
            ////////////////////

            // Terminate on a ^C
            signal (SIGINT, terminate);
            // Terminate on a kill/slay
            signal (SIGTERM, terminate);
            // Terminate on a shutdown
            signal (SIGPWR, terminate);

            // Create an NCManager object to manage the objects
            NetControllerMain::NCManager *lNCManager;
            lNCManager = new NetControllerMain::NCManager ( argv[1] );
            lNCManager->start();

            // Terminate with signals
            Time::TimeSpec lKeepAlivePeriod( 0.5 );
            do
            {
               nanosleep( lKeepAlivePeriod, NULL );
               lNCManager->keepAlive();
            } while (!gTerminate);

            std::cout << "Stopping the NetController due to signal" << 
std::endl;
            lNCManager->stop();
            lNCManager->join();
            delete lNCManager;
         }
         catch( Exception::ExceptionBase& aExc )
         {
            lSuccessCode = -1;

            std::cout << "Exception thrown from NCMananger: " << 
aExc.errorMessage() << std::endl;
         }
         catch( ... )
         {
            lSuccessCode = -1;

            std::cout << "Unknown exception thrown from NCMananger." << 
std::endl;
         }
         delete lProcessName;
      }
   }

   std::cout << std::endl << "Exiting NetController application... " << 
std::endl;
   std::cout << "--------------------------------------------------" << 
std::endl;

   return lSuccessCode;
}
