package LinuxTest.views;

import org.eclipse.jface.action.Action;
import org.eclipse.jface.viewers.TableViewer;
import org.eclipse.swt.SWT;
import org.eclipse.swt.custom.CTabFolder;
import org.eclipse.swt.custom.CTabItem;
import org.eclipse.swt.layout.GridData;
import org.eclipse.swt.layout.GridLayout;
import org.eclipse.swt.widgets.Composite;
import org.eclipse.swt.widgets.Text;
import org.eclipse.ui.part.ViewPart;

public class SampleView extends ViewPart {
	private TableViewer viewer;
	private Action action1;
	private Action action2;
	private Action doubleClickAction;



	/**
	 * The constructor.
	 */
	public SampleView() {
	}

	/**
	 * This is a callback that will allow us
	 * to create the viewer and initialize it.
	 */
	public void createPartControl(Composite parent) {

		GridData gd;
		GridLayout gridLayout = new GridLayout();
		gridLayout.marginWidth = 0;
		gridLayout.marginHeight = 0;
		gridLayout.horizontalSpacing = 0;
		gridLayout.verticalSpacing = 0;

		Composite displayArea = new Composite(parent, SWT.NONE);
		displayArea.setLayout(gridLayout);


		CTabFolder pageArea = new CTabFolder(displayArea, SWT.TOP);
		pageArea.setTabHeight(16);

		gd = new GridData(GridData.HORIZONTAL_ALIGN_FILL);
		gd.verticalAlignment = GridData.FILL;
		gd.grabExcessVerticalSpace = true;
		gd.grabExcessHorizontalSpace = true;
		pageArea.setLayoutData(gd);

		//Page 1 - the Message Page
		CTabItem messageTab = new CTabItem(pageArea, SWT.NULL);
		messageTab.setText("First tab");
		Text messageArea =
			new Text(
				pageArea,
				SWT.V_SCROLL
					| SWT.MULTI
					| SWT.BORDER
					| SWT.READ_ONLY
					| SWT.WRAP);

		messageArea.setText("This is tab 1");
		messageTab.setControl(messageArea);

	}


	/**
	 * Passing the focus request to the viewer's control.
	 */
	public void setFocus() {
	//	viewer.getControl().setFocus();
	}
}
