public class JmfTest {

	private final static Logger logger = Logger.getLogger(JmfTest.class.
getName());

	public static void main(String[] args) throws Exception {

		final Player player = Manager.createRealizedPlayer(CaptureDeviceManager.
getDevice("vfw:Microsoft WDM Image Capture (Win32):0").getLocator());
		final JOptionPane optionPane = new JOptionPane(player.
getVisualComponent(), JOptionPane.PLAIN_MESSAGE, JOptionPane.OK_CANCEL_OPTION);
		final JDialog imageCapturingDialog = optionPane.createDialog(null, 
"Capture Image");
		player.start();
		imageCapturingDialog.show();
		player.stop();

		final Control[] playerControls = player.getControls();
		for (int i = 0;(i < playerControls.length); i++) {

			final Control playerControl = playerControls[i];
			if (playerControl instanceof FrameGrabbingControl) {

				final FrameGrabbingControl frameGrabbingControl = 
(FrameGrabbingControl) playerControl;
				final Buffer buffer = frameGrabbingControl.grabFrame();
				final Format format = buffer.getFormat();
				final VideoFormat videoFormat = (VideoFormat) format;
				final BufferToImage bufferToImage = new BufferToImage(videoFormat)
;
				final Image capturedImage = bufferToImage.createImage(buffer);

				break;
			}
		}

		player.deallocate();
		player.close();
	}
}
