public class MultiThreadedLooper {

	public static void main(String[] args) {
		MultiThreadedLooper mtl = new MultiThreadedLooper();
		mtl.spawnSecondThread();
		try {
			Thread.sleep(2000);
		} catch (InterruptedException ie) {
		}
		mtl.go();
	}

	public void go() {
		System.out.println("Thread1: breakpoint here");
	}

	public void spawnSecondThread() {
		Thread secondThread = new Thread(new Runnable() {
			public void run() {
				for (int i = 0; i < 10; i++) {
					System.out.println("Thread2: looping " 
+ i);
					try {
						Thread.sleep(1000);
					} catch (InterruptedException ie) {	

					}
				}
			}
		});
		secondThread.setName("Second thread");
		secondThread.start();
	}
}
