	/**
	 * Method to restore the width of the perspective bar
	 */
	public void restoreState(IMemento memento) {
		if (memento == null)
			return;
		// restore the width of the perspective bar
		IMemento attributes = memento.getChild
(IWorkbenchConstants.TAG_PERSPECTIVE_BAR);
		IMemento size = null;
		if (attributes != null)
			size = attributes.getChild
(IWorkbenchConstants.TAG_ITEM_SIZE);
		if (size != null) {
			if (topBar != null)
				topBar.setRightWidth(size.getInteger
(IWorkbenchConstants.TAG_X).intValue());
		}
	}
