	public ServiceHome connectToServiceBean() {
		Hashtable env = new Hashtable();

		env.put(Context.INITIAL_CONTEXT_FACTORY,
				"com.ibm.websphere.naming.WsnInitialContextFactory");
		env.put(Context.PROVIDER_URL, "iiop://localhost:2809");
		try {

			ctx = new InitialContext(env);
			if (ctx == null)
				System.out.println("Couldn't get initial context");
			Object homeObject = ctx.lookup("ejb/survey/ejb/ServiceHome");
			ServiceHome servHome = (ServiceHome) PortableRemoteObject.narrow(
					homeObject, ServiceHome.class);
			service = servHome.create();
			System.out.println("Created service bean successfully!!!");
			return servHome;
		} catch (NamingException ex) {
			System.out.println(ex.toString());
		} catch (RemoteException rex) {
			System.out
					.println("Remote exception while creating the service bean");
		} catch (CreateException cex) {
			System.out
					.println("Could not create a service session bean on the Websphere server");
		}

		return null;
	}

